import React, { useEffect } from "react";
import { Stethoscope, Code, BarChart2, LifeBuoy, SlidersHorizontal } from "lucide-react";

export default function LandingPage() {
  useEffect(() => {
    const links = document.querySelectorAll("a[href^='#']");
    links.forEach((link) => {
      const href = link.getAttribute("href");
      if (href && href !== "#") {
        link.addEventListener("click", (e) => {
          e.preventDefault();
          const target = document.querySelector(href);
          if (target) {
            target.scrollIntoView({ behavior: "smooth" });
          }
        });
      }
    });
  }, []);

  const solucoes = [
    {
      title: "Consultoria Hospitalar",
      description: "Consultorias especializadas para otimizar processos clínicos e administrativos.",
      icon: <Stethoscope className="w-10 h-10 text-blue-400 mb-4 transition-colors duration-300 group-hover:text-blue-700" />
    },
    {
      title: "Desenvolvimento de Software",
      description: "Sistemas personalizados para gestão, controle e inovação da sua empresa.",
      icon: <Code className="w-10 h-10 text-blue-400 mb-4 transition-colors duration-300 group-hover:text-blue-700" />
    },
    {
      title: "Inteligência de Dados",
      description: "Transforme dados em decisões estratégicas com nossas soluções de BI e análise.",
      icon: <BarChart2 className="w-10 h-10 text-blue-400 mb-4 transition-colors duration-300 group-hover:text-blue-700" />
    },
    {
      title: "Suporte Técnico",
      description: "Equipe pronta para garantir a operação contínua e segura dos seus sistemas.",
      icon: <LifeBuoy className="w-10 h-10 text-blue-400 mb-4 transition-colors duration-300 group-hover:text-blue-700" />
    },
    {
      title: "Soluções Sob Medida",
      description: "Desenvolvimento de sistemas personalizados conforme sua demanda.",
      icon: <SlidersHorizontal className="w-10 h-10 text-blue-400 mb-4 transition-colors duration-300 group-hover:text-blue-700" />
    }
  ];

  return (
    <div className="min-h-screen bg-blue-50 text-black">
      {/* Header */}
      <header className="fixed top-0 left-0 w-full z-50 flex items-center justify-between px-4 md:px-6 py-2 bg-white shadow-md">
        <div className="flex items-center gap-6">
          <img src="/logo-ma-tecnologia.png" alt="MA Tecnologia Logo" className="h-20 w-auto object-contain" />
          <nav className="flex items-center gap-10 text-lg font-semibold">
            <a href="#hero" className="text-black hover:text-white hover:bg-blue-700 px-4 py-2 rounded transition-all">Home</a>
            <a href="#sobre" className="text-black hover:text-white hover:bg-blue-700 px-4 py-2 rounded transition-all">Sobre</a>
            <a href="#solucoes" className="text-black hover:text-white hover:bg-blue-700 px-4 py-2 rounded transition-all">Soluções</a>
            <a href="#contato" className="text-black hover:text-white hover:bg-blue-700 px-4 py-2 rounded transition-all">Contato</a>
          </nav>
        </div>
        <div className="flex items-center gap-3">
          <a href="#contato" className="bg-gradient-to-r from-blue-600 to-blue-800 text-white px-5 py-2 rounded-full shadow hover:brightness-110 transition-all duration-300">Fale Conosco</a>
          <a href="#cliente" className="bg-white border border-blue-700 text-blue-700 px-5 py-2 rounded-full hover:bg-blue-700 hover:text-white transition-all duration-300">Já sou cliente</a>
        </div>
      </header>

      {/* Hero Section com vídeo de fundo */}
      <section id="hero" className="relative scroll-mt-32 h-[650px] flex items-center justify-center overflow-hidden">
        <video
          autoPlay
          loop
          muted
          playsInline
          className="absolute top-0 left-0 w-full h-full object-cover"
          src="/medico-fundo.mp4"
          type="video/mp4"
        />

        <div className="relative z-10 bg-white bg-opacity-80 p-8 rounded-xl max-w-2xl text-center">
          <span className="inline-block bg-blue-100 text-blue-800 text-sm font-semibold px-3 py-1 rounded-full mb-4">
            Soluções Tecnológicas
          </span>
          <h1 className="text-4xl md:text-6xl font-bold mb-4 text-black">
            Excelência em Sistemas para sua Empresa
          </h1>
          <p className="text-lg text-gray-700 mb-6">
            Desenvolvemos softwares que transformam a gestão de clínicas, eventos e negócios com eficiência e inovação.
          </p>
          <a href="#contato" className="bg-blue-700 text-white px-6 py-3 rounded-lg font-semibold hover:bg-blue-800">
            Solicitar Demonstração
          </a>
        </div>
      </section>

      {/* Soluções */}
      <section id="solucoes" className="scroll-mt-32 py-20 px-8">
        <h2 className="text-3xl font-bold text-center mb-12 text-black">Nossas Soluções</h2>
        <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 xl:grid-cols-4 gap-10">
          {solucoes.map((solucao, index) => (
            <div key={index} className="bg-white rounded-xl shadow-md hover:shadow-xl transition-all border border-gray-200 p-6 flex flex-col justify-between group">
              <div>
                {solucao.icon}
                <h3 className="text-xl font-semibold text-blue-800 mb-2">{solucao.title}</h3>
                <p className="text-gray-600 text-sm leading-relaxed">{solucao.description}</p>
              </div>
              <div className="mt-4 text-right">
                <span className="inline-block text-sm text-blue-600 font-medium">Saiba mais →</span>
              </div>
            </div>
          ))}
        </div>
      </section>

      {/* Sobre */}
      <section id="sobre" className="scroll-mt-32 py-20 px-8 bg-gray-50">
        <h2 className="text-3xl font-bold text-center mb-8 text-black">Sobre a MA Tecnologia</h2>
        <p className="max-w-3xl mx-auto text-center text-gray-700 text-lg">
